import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { toast } from 'react-hot-toast';
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  PlayIcon,
  PauseIcon,
  CogIcon,
  TagIcon
} from '@heroicons/react/24/outline';
import axios from 'axios';
import ScrapingProgress from './ScrapingProgress';

const ScrapingConfigs = () => {
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingConfig, setEditingConfig] = useState(null);
  const [selectedConfigs, setSelectedConfigs] = useState([]);
  const [showGroupModal, setShowGroupModal] = useState(false);
  const [groupName, setGroupName] = useState('');
  const [filters, setFilters] = useState({ group: '' });
  const [currentScrapingJob, setCurrentScrapingJob] = useState(null);
  const [scrapingProgress, setScrapingProgress] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    industry_type: '',
    keywords: [''],
    sources: ['scrapy', 'google', 'bing', 'news'],
    frequency: 'daily',
    max_results_per_run: 50,
    location: '',
    search_query: '',
    group: '',
    is_active: true
  });

  const [fieldErrors, setFieldErrors] = useState({});
  const [generalError, setGeneralError] = useState('');

  const queryClient = useQueryClient();

  // Fetch scraping configurations
  const { data: configsData, isLoading, error } = useQuery(
    ['scrapingConfigs'],
    async () => {
      const response = await axios.get('/api/scraping/configs');
      return response.data;
    }
  );

  // Fetch industries for dropdown
  const { data: industriesData } = useQuery(
    ['industries'],
    async () => {
      const response = await axios.get('/api/industries');
      return response.data;
    }
  );

  // Create configuration mutation
  const createConfigMutation = useMutation(
    async (configData) => {
      const response = await axios.post('/api/scraping/configs', configData);
      return response.data;
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['scrapingConfigs']);
        toast.success('Configuration created successfully');
        setShowCreateModal(false);
        resetForm();
        setFieldErrors({});
        setGeneralError('');
      },
      onError: (error) => {
        const errorData = error.response?.data;
        if (errorData?.fieldErrors) {
          // Handle field-specific errors
          const errors = {};
          errorData.fieldErrors.forEach(err => {
            errors[err.field] = err.message;
          });
          setFieldErrors(errors);
          setGeneralError(errorData.message || 'Please fix the highlighted fields below');
        } else {
          // Handle general errors
          setGeneralError(errorData?.error || 'Failed to create configuration');
          setFieldErrors({});
        }
        toast.error(errorData?.error || 'Failed to create configuration');
      }
    }
  );

  // Update configuration mutation
  const updateConfigMutation = useMutation(
    async ({ id, data }) => {
      const response = await axios.put(`/api/scraping/configs/${id}`, data);
      return response.data;
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['scrapingConfigs']);
        toast.success('Configuration updated successfully');
        setShowEditModal(false);
        setEditingConfig(null);
        resetForm();
        setFieldErrors({});
        setGeneralError('');
      },
      onError: (error) => {
        const errorData = error.response?.data;
        if (errorData?.fieldErrors) {
          // Handle field-specific errors
          const errors = {};
          errorData.fieldErrors.forEach(err => {
            errors[err.field] = err.message;
          });
          setFieldErrors(errors);
          setGeneralError(errorData.message || 'Please fix the highlighted fields below');
        } else {
          // Handle general errors
          setGeneralError(errorData?.error || 'Failed to update configuration');
          setFieldErrors({});
        }
        toast.error(errorData?.error || 'Failed to update configuration');
      }
    }
  );

  // Toggle configuration status mutation
  const toggleStatusMutation = useMutation(
    async ({ id, isActive }) => {
      const response = await axios.patch(`/api/scraping/configs/${id}/toggle`, { is_active: isActive });
      return response.data;
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['scrapingConfigs']);
        toast.success('Configuration status updated');
      },
      onError: (error) => {
        toast.error(error.response?.data?.error || 'Failed to update status');
      }
    }
  );

  // Delete configuration mutation
  const deleteConfigMutation = useMutation(
    async (id) => {
      await axios.delete(`/api/scraping/configs/${id}`);
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(['scrapingConfigs']);
        toast.success('Configuration deleted successfully');
      },
      onError: (error) => {
        toast.error(error.response?.data?.error || 'Failed to delete configuration');
      }
    }
  );

  // Bulk delete configurations
  const bulkDeleteConfigs = () => {
    if (selectedConfigs.length === 0) return;

    if (window.confirm(`Are you sure you want to delete ${selectedConfigs.length} configuration(s)?`)) {
      selectedConfigs.forEach(id => deleteConfigMutation.mutate(id));
      setSelectedConfigs([]);
    }
  };

  // Bulk group update
  const handleBulkGroupUpdate = () => {
    if (selectedConfigs.length === 0 || !groupName.trim()) return;

    // Update each selected configuration with the new group
    selectedConfigs.forEach(id => {
      const config = configsData?.configs?.find(c => c.id === id);
      if (config) {
        updateConfigMutation.mutate({
          id,
          data: { ...config, group: groupName.trim() }
        });
      }
    });

    setGroupName('');
    setShowGroupModal(false);
    setSelectedConfigs([]);
  };

  // Handle configuration selection
  const handleConfigSelection = (configId) => {
    setSelectedConfigs(prev => 
      prev.includes(configId)
        ? prev.filter(id => id !== configId)
        : [...prev, configId]
    );
  };

  // Handle select all
  const handleSelectAll = () => {
    if (selectedConfigs.length === configsData?.configs?.length) {
      setSelectedConfigs([]);
    } else {
      setSelectedConfigs(configsData?.configs?.map(config => config.id) || []);
    }
  };

  // Run configuration mutation
  const runConfigMutation = useMutation(
    async (id) => {
      const response = await axios.post(`/api/scraping/configs/${id}/run`);
      return response.data;
    },
    {
      onSuccess: (data, variables) => {
        // Start progress tracking for enhanced scraping
        const jobId = `config-${variables}-${Date.now()}`;
        setCurrentScrapingJob({ id: variables, jobId });
        setScrapingProgress({ stage: 'initializing', progress: 0, total: 1, percentage: 0, message: 'Starting enhanced scraping...' });
        toast.success('Enhanced scraping job started - searching high-quality sources');
      },
      onError: (error) => {
        toast.error(error.response?.data?.error || 'Failed to start scraping job');
        setCurrentScrapingJob(null);
        setScrapingProgress(null);
      }
    }
  );

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      industry_type: '',
      keywords: [''],
      sources: ['scrapy', 'google', 'bing', 'news'],
      frequency: 'daily',
      max_results_per_run: 50,
      location: '',
      search_query: '',
      is_active: true
    });
    setFieldErrors({});
    setGeneralError('');
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear field error when user starts typing
    if (fieldErrors[field]) {
      setFieldErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleKeywordsChange = (index, value) => {
    const newKeywords = [...formData.keywords];
    newKeywords[index] = value;

    // Add new empty keyword field if this is the last one and it's not empty
    if (index === newKeywords.length - 1 && value && !newKeywords.includes('')) {
      newKeywords.push('');
    }

    // Remove empty keywords except the last one
    const filteredKeywords = newKeywords.filter((keyword, i) => 
      keyword || i === newKeywords.length - 1
    );

    setFormData(prev => ({ ...prev, keywords: filteredKeywords }));

    // Clear keywords error when user starts typing
    if (fieldErrors.keywords) {
      setFieldErrors(prev => ({ ...prev, keywords: '' }));
    }
  };

  const handleSourcesChange = (sources) => {
    setFormData(prev => ({ ...prev, sources }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Clear previous errors
    setFieldErrors({});
    setGeneralError('');

    // Filter out empty keywords
    const filteredKeywords = formData.keywords.filter(keyword => keyword.trim());

    if (filteredKeywords.length === 0) {
      setFieldErrors(prev => ({ ...prev, keywords: 'At least one keyword is required' }));
      return;
    }

    const submitData = {
      ...formData,
      keywords: filteredKeywords
    };

    if (editingConfig) {
      updateConfigMutation.mutate({ id: editingConfig.id, data: submitData });
    } else {
      createConfigMutation.mutate(submitData);
    }
  };

  const handleEdit = (config) => {
    setEditingConfig(config);
    setFormData({
      name: config.name,
      description: config.description || '',
      industry_type: config.industry_type || '',
      keywords: config.keywords.length > 0 ? [...config.keywords, ''] : [''],
      sources: config.sources.length > 0 ? config.sources : ['scrapy', 'google', 'bing', 'news'],
      frequency: config.frequency || 'daily',
      max_results_per_run: config.max_results_per_run || 50,
      location: config.location || '',
      search_query: config.search_query || '',
      is_active: config.is_active
    });
    setFieldErrors({});
    setGeneralError('');
    setShowEditModal(true);
  };

  const handleDelete = (config) => {
    if (window.confirm(`Are you sure you want to delete "${config.name}"?`)) {
      deleteConfigMutation.mutate(config.id);
    }
  };

  const handleToggleStatus = (config) => {
    toggleStatusMutation.mutate({
      id: config.id,
      isActive: !config.is_active
    });
  };

  const handleRunNow = (config) => {
    if (window.confirm(`Run scraping job for "${config.name}" now?`)) {
      runConfigMutation.mutate(config.id);
    }
  };

  const getFrequencyLabel = (frequency) => {
    const labels = {
      hourly: 'Hourly',
      daily: 'Daily',
      weekly: 'Weekly',
      monthly: 'Monthly',
      custom: 'Custom'
    };
    return labels[frequency] || frequency;
  };

  // Helper function to get field error styling
  const getFieldErrorStyle = (fieldName) => {
    return fieldErrors[fieldName] 
      ? 'border-red-300 focus:border-red-500 focus:ring-red-500' 
      : 'border-gray-300 focus:border-indigo-500 focus:ring-indigo-500';
  };

  if (error) {
    return (
      <div className="p-6">
        <div className="bg-red-50 border border-red-200 rounded-md p-4">
          <p className="text-red-800">Error loading configurations: {error.message}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Scraping Configurations</h1>
          <p className="text-gray-600">Manage your automated lead scraping jobs</p>
          <div className="mt-2 p-2 bg-green-50 border border-green-200 rounded-md">
            <p className="text-sm text-green-700">
              ðŸš€ <strong>Enhanced Scraping Service Active:</strong> All scraping jobs now use our advanced technology 
              for high-quality lead generation with AI-powered data extraction.
            </p>
          </div>
        </div>
        <div className="flex items-center space-x-3">
          <select
            value={filters?.group || ''}
            onChange={(e) => setFilters(prev => ({ ...prev, group: e.target.value }))}
            className="block px-3 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
          >
            <option value="">All Groups</option>
            {configsData?.configs?.reduce((groups, config) => {
              if (config.group && !groups.includes(config.group)) {
                groups.push(config.group);
              }
              return groups;
            }, []).map(group => (
              <option key={group} value={group}>{group}</option>
            ))}
          </select>
          <button
            onClick={() => setShowCreateModal(true)}
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            <PlusIcon className="h-4 w-4 mr-2" />
            New Configuration
          </button>
        </div>
      </div>

      {/* Bulk Actions */}
      {selectedConfigs.length > 0 && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <span className="text-sm font-medium text-blue-800">
                {selectedConfigs.length} configuration(s) selected
              </span>

              <button
                onClick={bulkDeleteConfigs}
                className="inline-flex items-center px-3 py-2 border border-red-300 rounded-md text-sm font-medium text-red-700 bg-white hover:bg-red-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
              >
                <TrashIcon className="h-4 w-4 mr-1" />
                Delete All
              </button>

              <button
                onClick={() => setShowGroupModal(true)}
                className="inline-flex items-center px-3 py-2 border border-blue-300 rounded-md text-sm font-medium text-blue-700 bg-white hover:bg-blue-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <TagIcon className="h-4 w-4 mr-1" />
                Group
              </button>
            </div>

            <button
              onClick={() => setSelectedConfigs([])}
              className="text-blue-600 hover:text-blue-800 text-sm font-medium"
            >
              Clear Selection
            </button>
          </div>
        </div>
      )}

      {/* Enhanced Scraping Progress */}
      {currentScrapingJob && scrapingProgress && (
        <div className="mb-6">
          <ScrapingProgress 
            jobId={currentScrapingJob.jobId}
            onComplete={() => {
              setCurrentScrapingJob(null);
              setScrapingProgress(null);
              queryClient.invalidateQueries(['scrapingConfigs']);
              toast.success('Enhanced scraping completed! High-quality leads have been found and saved.');
            }}
            onError={() => {
              setCurrentScrapingJob(null);
              setScrapingProgress(null);
              toast.error('Scraping job failed. Please try again.');
            }}
          />
        </div>
      )}

      {/* Configurations List */}
      {isLoading ? (
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600 mx-auto"></div>
          <p className="mt-2 text-gray-600">Loading configurations...</p>
        </div>
      ) : (
        <div className="bg-white shadow overflow-hidden sm:rounded-md">
          {configsData?.configs?.filter(config => !filters.group || config.group === filters.group).length === 0 ? (
            <div className="text-center py-12">
              <CogIcon className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No configurations</h3>
              <p className="mt-1 text-sm text-gray-500">
                Get started by creating your first scraping configuration.
              </p>
              <div className="mt-6">
                <button
                  onClick={() => setShowCreateModal(true)}
                  className="inline-flex items-center px-4 py-2 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  <PlusIcon className="h-4 w-4 mr-2" />
                  New Configuration
                </button>
              </div>
            </div>
          ) : (
            <ul className="divide-y divide-gray-200">
              {configsData?.configs?.filter(config => !filters.group || config.group === filters.group).map((config) => (
                <li key={config.id} className="px-6 py-4">
                  <div className="flex items-center justify-between">
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center space-x-3">
                        <input
                          type="checkbox"
                          checked={selectedConfigs.includes(config.id)}
                          onChange={() => handleConfigSelection(config.id)}
                          className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                        />
                        <div className="flex-shrink-0">
                          <CogIcon className="h-6 w-6 text-gray-400" />
                        </div>
                        <div className="flex-1 min-w-0">
                          <p className="text-sm font-medium text-gray-900 truncate">
                            {config.name}
                          </p>
                          <p className="text-sm text-gray-500 truncate">
                            {config.description || 'No description'}
                          </p>
                          <div className="mt-1 flex items-center space-x-4 text-xs text-gray-500">
                            <span>Industry: {config.industry_type || 'Any'}</span>
                            <span>Frequency: {getFrequencyLabel(config.frequency)}</span>
                            <span>Keywords: {config.keywords.length}</span>
                            <span>Max Results: {config.max_results_per_run}</span>
                            {config.group && <span>Group: {config.group}</span>}
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => handleRunNow(config)}
                        disabled={!config.is_active}
                        className="inline-flex items-center px-2 py-1 border border-gray-300 rounded text-xs font-medium text-gray-700 bg-white hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                        title="Run now"
                      >
                        <PlayIcon className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => handleToggleStatus(config)}
                        className={`inline-flex items-center px-2 py-1 border rounded text-xs font-medium ${
                          config.is_active
                            ? 'border-green-300 text-green-700 bg-green-50 hover:bg-green-100'
                            : 'border-gray-300 text-gray-700 bg-gray-50 hover:bg-gray-100'
                        }`}
                        title={config.is_active ? 'Deactivate' : 'Activate'}
                      >
                        {config.is_active ? (
                          <PauseIcon className="h-4 w-4" />
                        ) : (
                          <PlayIcon className="h-4 w-4" />
                        )}
                      </button>
                      <button
                        onClick={() => handleEdit(config)}
                        className="inline-flex items-center px-2 py-1 border border-gray-300 rounded text-xs font-medium text-gray-700 bg-white hover:bg-gray-50"
                        title="Edit"
                      >
                        <PencilIcon className="h-4 w-4" />
                      </button>
                      <button
                        onClick={() => handleDelete(config)}
                        className="inline-flex items-center px-2 py-1 border border-red-300 rounded text-xs font-medium text-red-700 bg-white hover:bg-red-50"
                        title="Delete"
                      >
                        <TrashIcon className="h-4 w-4" />
                      </button>
                    </div>
                  </div>
                  {config.keywords.length > 0 && (
                    <div className="mt-2 flex flex-wrap gap-1">
                      {config.keywords.map((keyword, index) => (
                        <span
                          key={index}
                          className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800"
                        >
                          {keyword}
                        </span>
                      ))}
                    </div>
                  )}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Create/Edit Modal */}
      {(showCreateModal || showEditModal) && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-96 shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <h3 className="text-lg font-medium text-gray-900 mb-4">
                {editingConfig ? 'Edit Configuration' : 'New Configuration'}
              </h3>

              {/* Enhanced Scraping Service Note */}
              <div className="mb-4 p-3 bg-blue-50 border border-blue-200 rounded-md">
                <p className="text-sm text-blue-800">
                  <strong>ðŸš€ Enhanced Scraping Service:</strong> This configuration will use our advanced scraping technology 
                  to find high-quality business leads from multiple sources, with AI-powered data extraction and full content analysis.
                </p>
              </div>

              {/* General Error Display */}
              {generalError && (
                <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-md">
                  <p className="text-sm text-red-800">{generalError}</p>
                </div>
              )}

              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Name *</label>
                  <input
                    type="text"
                    required
                    value={formData.name}
                    onChange={(e) => handleInputChange('name', e.target.value)}
                    className={`mt-1 block w-full border rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-offset-2 ${getFieldErrorStyle('name')}`}
                  />
                  {fieldErrors.name && (
                    <p className="mt-1 text-sm text-red-600">{fieldErrors.name}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Description</label>
                  <textarea
                    value={formData.description}
                    onChange={(e) => handleInputChange('description', e.target.value)}
                    rows={3}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Industry Type</label>
                  <select
                    value={formData.industry_type}
                    onChange={(e) => handleInputChange('industry_type', e.target.value)}
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  >
                    <option value="">Any Industry</option>
                    {industriesData?.industries?.map((industry) => (
                      <option key={industry.id} value={industry.name}>
                        {industry.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Keywords *</label>
                  <p className="text-xs text-gray-500 mb-2">
                    Enter multiple keywords. You can separate them with commas or use separate lines.
                  </p>
                  {formData.keywords.map((keyword, index) => (
                    <input
                      key={index}
                      type="text"
                      value={keyword}
                      onChange={(e) => handleKeywordsChange(index, e.target.value)}
                      placeholder={index === 0 ? "e.g., New Boutique Hotel Projects" : "Additional keyword"}
                      className={`mt-1 block w-full border rounded-md px-3 py-2 focus:outline-none focus:ring-2 focus:ring-offset-2 ${getFieldErrorStyle('keywords')}`}
                    />
                  ))}
                  {fieldErrors.keywords && (
                    <p className="mt-1 text-sm text-red-600">{fieldErrors.keywords}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Sources</label>
                  <p className="mt-1 text-sm text-gray-500 mb-2">
                    Select high-quality sources for enhanced lead generation. We recommend using multiple sources for best results.
                  </p>
                  <div className="mt-2 space-y-2">
                    {[
                      { key: 'scrapy', label: 'Scrapy API', description: 'High-quality business news scraping' },
                      { key: 'google', label: 'Google News', description: 'Latest news and announcements' },
                      { key: 'bing', label: 'Bing News', description: 'Alternative news source' },
                      { key: 'news', label: 'Business News', description: 'Industry-specific news sites' },
                      { key: 'rss', label: 'RSS Feeds', description: 'Direct feed monitoring (fallback)' }
                    ].map((source) => (
                      <label key={source.key} className="flex items-start">
                        <input
                          type="checkbox"
                          checked={formData.sources.includes(source.key)}
                          onChange={(e) => {
                            if (e.target.checked) {
                              handleSourcesChange([...formData.sources, source.key]);
                            } else {
                              handleSourcesChange(formData.sources.filter(s => s !== source.key));
                            }
                          }}
                          className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded mt-0.5"
                        />
                        <div className="ml-2">
                          <span className="text-sm font-medium text-gray-700">{source.label}</span>
                          <p className="text-xs text-gray-500">{source.description}</p>
                        </div>
                      </label>
                    ))}
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Frequency</label>
                    <select
                      value={formData.frequency}
                      onChange={(e) => handleInputChange('frequency', e.target.value)}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    >
                      <option value="hourly">Hourly</option>
                      <option value="daily">Daily</option>
                      <option value="weekly">Weekly</option>
                      <option value="monthly">Monthly</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700">Max Results</label>
                    <input
                      type="number"
                      min="1"
                      max="1000"
                      value={formData.max_results_per_run}
                      onChange={(e) => handleInputChange('max_results_per_run', parseInt(e.target.value))}
                      className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Location</label>
                  <input
                    type="text"
                    value={formData.location}
                    onChange={(e) => handleInputChange('location', e.target.value)}
                    placeholder="e.g., New York, CA, USA"
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Group</label>
                  <input
                    type="text"
                    value={formData.group}
                    onChange={(e) => handleInputChange('group', e.target.value)}
                    placeholder="Optional group name for organization"
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700">Additional Search Query</label>
                  <input
                    type="text"
                    value={formData.search_query}
                    onChange={(e) => handleInputChange('search_query', e.target.value)}
                    placeholder="Additional terms to include in search"
                    className="mt-1 block w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div className="flex items-center">
                  <input
                    type="checkbox"
                    checked={formData.is_active}
                    onChange={(e) => handleInputChange('is_active', e.target.checked)}
                    className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                  />
                  <label className="ml-2 block text-sm text-gray-900">Active</label>
                </div>

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => {
                      setShowCreateModal(false);
                      setShowEditModal(false);
                      setEditingConfig(null);
                      resetForm();
                    }}
                    className="px-4 py-2 border border-gray-300 rounded-md text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    disabled={createConfigMutation.isLoading || updateConfigMutation.isLoading}
                    className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
                  >
                    {createConfigMutation.isLoading || updateConfigMutation.isLoading
                      ? 'Saving...'
                      : editingConfig
                      ? 'Update'
                      : 'Create'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Group Modal */}
      {showGroupModal && (
        <GroupModal
          groupName={groupName}
          setGroupName={setGroupName}
          onClose={() => setShowGroupModal(false)}
          onSubmit={handleBulkGroupUpdate}
        />
      )}
    </div>
  );
};

