import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion';
import {
  PlusIcon,
  PencilIcon,
  TrashIcon,
  PlayIcon,
  PauseIcon,
  EyeIcon,
  Cog6ToothIcon,
  MagnifyingGlassIcon,
  GlobeAltIcon,
  DocumentTextIcon,
  ClockIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';

const ScrapingConfigs = () => {
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editingConfig, setEditingConfig] = useState(null);
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [keywords, setKeywords] = useState(['']);
  const [sources, setSources] = useState(['']);
  const [frequency, setFrequency] = useState('daily');
  const [maxResults, setMaxResults] = useState(50);
  const [location, setLocation] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [description, setDescription] = useState('');
  const [configName, setConfigName] = useState('');

  const queryClient = useQueryClient();

  // Fetch scraping configurations
  const { data: configs, isLoading } = useQuery('scrapingConfigs', async () => {
    const response = await axios.get('/api/scraping/configs');
    return response.data.configs || [];
  });

  // Fetch available industries
  const { data: industries } = useQuery('industries', async () => {
    const response = await axios.get('/api/industries');
    return response.data.industries || [];
  });

  // Fetch user subscription info
  const { data: userInfo } = useQuery('userInfo', async () => {
    const response = await axios.get('/api/users/profile');
    return response.data.user;
  });

  // Create new scraping config
  const createMutation = useMutation(
    async (configData) => {
      const response = await axios.post('/api/scraping/configs', configData);
      return response.data;
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries('scrapingConfigs');
        toast.success('Scraping configuration created successfully!');
        resetForm();
        setIsCreateModalOpen(false);
      },
      onError: (error) => {
        toast.error(error.response?.data?.error || 'Failed to create configuration');
      }
    }
  );

  // Update existing config
  const updateMutation = useMutation(
    async (configData) => {
      const response = await axios.put(`/api/scraping/configs/${editingConfig.id}`, configData);
      return response.data;
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries('scrapingConfigs');
        toast.success('Configuration updated successfully!');
        resetForm();
        setIsEditModalOpen(false);
        setEditingConfig(null);
      },
      onError: (error) => {
        toast.error(error.response?.data?.error || 'Failed to update configuration');
      }
    }
  );

  // Toggle config status
  const toggleStatusMutation = useMutation(
    async ({ id, isActive }) => {
      const response = await axios.patch(`/api/scraping/configs/${id}/toggle`, { isActive });
      return response.data;
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries('scrapingConfigs');
        toast.success('Configuration status updated!');
      },
      onError: (error) => {
        toast.error(error.response?.data?.error || 'Failed to update status');
      }
    }
  );

  // Delete config
  const deleteMutation = useMutation(
    async (id) => {
      await axios.delete(`/api/scraping/configs/${id}`);
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries('scrapingConfigs');
        toast.success('Configuration deleted successfully!');
      },
      onError: (error) => {
        toast.error(error.response?.data?.error || 'Failed to delete configuration');
      }
    }
  );

  const resetForm = () => {
    setConfigName('');
    setDescription('');
    setSelectedIndustry('');
    setKeywords(['']);
    setSources(['']);
    setFrequency('daily');
    setMaxResults(50);
    setLocation('');
    setSearchQuery('');
  };

  const handleEdit = (config) => {
    setEditingConfig(config);
    setConfigName(config.name);
    setDescription(config.description || '');
    setSelectedIndustry(config.industry || '');
    setKeywords(config.keywords || ['']);
    setSources(config.sources || ['']);
    setFrequency(config.frequency || 'daily');
    setMaxResults(config.maxResultsPerRun || 50);
    setLocation(config.location || '');
    setSearchQuery(config.searchQuery || '');
    setIsEditModalOpen(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!configName.trim()) {
      toast.error('Configuration name is required');
      return;
    }

    if (keywords.filter(k => k.trim()).length === 0) {
      toast.error('At least one keyword is required');
      return;
    }

    const configData = {
      name: configName.trim(),
      description: description.trim(),
      industry: selectedIndustry,
      keywords: keywords.filter(k => k.trim()),
      sources: sources.filter(s => s.trim()),
      frequency,
      maxResultsPerRun: maxResults,
      location: location.trim(),
      searchQuery: searchQuery.trim(),
      isActive: true
    };

    if (editingConfig) {
      updateMutation.mutate(configData);
    } else {
      createMutation.mutate(configData);
    }
  };

  const addKeyword = () => {
    setKeywords([...keywords, '']);
  };

  const removeKeyword = (index) => {
    setKeywords(keywords.filter((_, i) => i !== index));
  };

  const updateKeyword = (index, value) => {
    const newKeywords = [...keywords];
    newKeywords[index] = value;
    setKeywords(newKeywords);
  };

  const addSource = () => {
    setSources([...sources, '']);
  };

  const removeSource = (index) => {
    setSources(sources.filter((_, i) => i !== index));
  };

  const updateSource = (index, value) => {
    const newSources = [...sources];
    newSources[index] = value;
    setSources(newSources);
  };

  const getRemainingConfigs = () => {
    const maxConfigs = userInfo?.maxScrapingConfigs || 3;
    const activeConfigs = configs?.filter(c => c.isActive)?.length || 0;
    return Math.max(0, maxConfigs - activeConfigs);
  };

  const getSubscriptionTier = () => {
    return userInfo?.subscriptionTier || 'free';
  };

  const getSubscriptionLimits = () => {
    const limits = {
      free: { maxConfigs: 3, maxResults: 50, frequency: 'daily' },
      basic: { maxConfigs: 10, maxResults: 100, frequency: 'daily' },
      premium: { maxConfigs: 25, maxResults: 200, frequency: 'hourly' },
      enterprise: { maxConfigs: 100, maxResults: 500, frequency: 'hourly' }
    };
    return limits[getSubscriptionTier()] || limits.free;
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Scraping Configurations</h1>
          <p className="mt-1 text-sm text-gray-500">
            Set up automated lead discovery rules for your industry
          </p>
        </div>
        <button
          onClick={() => setIsCreateModalOpen(true)}
          disabled={getRemainingConfigs() === 0}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          <PlusIcon className="h-4 w-4 mr-2" />
          New Configuration
        </button>
      </div>

      {/* Subscription Info */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex items-center justify-between">
          <div>
            <h3 className="text-lg font-medium text-gray-900">
              {getSubscriptionTier().charAt(0).toUpperCase() + getSubscriptionTier().slice(1)} Plan
            </h3>
            <p className="text-sm text-gray-500">
              {getRemainingConfigs()} of {getSubscriptionLimits().maxConfigs} configurations remaining
            </p>
          </div>
          <div className="text-right">
            <p className="text-sm text-gray-500">Max results per run</p>
            <p className="text-lg font-semibold text-gray-900">{getSubscriptionLimits().maxResults}</p>
          </div>
        </div>
      </div>

      {/* Configurations List */}
      <div className="bg-white shadow rounded-lg">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-medium text-gray-900">Active Configurations</h3>
        </div>
        <div className="divide-y divide-gray-200">
          {configs?.length === 0 ? (
            <div className="p-6 text-center text-gray-500">
              <DocumentTextIcon className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">No configurations yet</h3>
              <p className="mt-1 text-sm text-gray-500">
                Create your first scraping configuration to start discovering leads.
              </p>
            </div>
          ) : (
            configs?.map((config) => (
              <div key={config.id} className="p-6">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3">
                      <h4 className="text-lg font-medium text-gray-900">{config.name}</h4>
                      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                        config.isActive 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-gray-100 text-gray-800'
                      }`}>
                        {config.isActive ? 'Active' : 'Inactive'}
                      </span>
                    </div>
                    {config.description && (
                      <p className="mt-1 text-sm text-gray-500">{config.description}</p>
                    )}
                    <div className="mt-3 flex flex-wrap gap-2">
                      {config.industry && (
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                          {config.industry}
                        </span>
                      )}
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-purple-100 text-purple-800">
                        {config.frequency}
                      </span>
                      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                        Max {config.maxResultsPerRun} results
                      </span>
                    </div>
                    <div className="mt-3">
                      <p className="text-sm text-gray-600">
                        <strong>Keywords:</strong> {config.keywords.join(', ')}
                      </p>
                      {config.sources.length > 0 && (
                        <p className="text-sm text-gray-600 mt-1">
                          <strong>Sources:</strong> {config.sources.join(', ')}
                        </p>
                      )}
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => handleEdit(config)}
                      className="p-2 text-gray-400 hover:text-gray-600"
                    >
                      <PencilIcon className="h-5 w-5" />
                    </button>
                    <button
                      onClick={() => toggleStatusMutation.mutate({ 
                        id: config.id, 
                        isActive: !config.isActive 
                      })}
                      className={`p-2 ${
                        config.isActive 
                          ? 'text-yellow-400 hover:text-yellow-600' 
                          : 'text-green-400 hover:text-green-600'
                      }`}
                    >
                      {config.isActive ? (
                        <PauseIcon className="h-5 w-5" />
                      ) : (
                        <PlayIcon className="h-5 w-5" />
                      )}
                    </button>
                    <button
                      onClick={() => deleteMutation.mutate(config.id)}
                      className="p-2 text-red-400 hover:text-red-600"
                    >
                      <TrashIcon className="h-5 w-5" />
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Create/Edit Modal */}
      <AnimatePresence>
        {(isCreateModalOpen || isEditModalOpen) && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 overflow-y-auto"
          >
            <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
              <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" />

              <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-2xl sm:w-full">
                <form onSubmit={handleSubmit}>
                  <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                    <div className="sm:flex sm:items-start">
                      <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-primary-100 sm:mx-0 sm:h-10 sm:w-10">
                        <Cog6ToothIcon className="h-6 w-6 text-primary-600" />
                      </div>
                      <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                        <h3 className="text-lg leading-6 font-medium text-gray-900">
                          {editingConfig ? 'Edit Configuration' : 'New Scraping Configuration'}
                        </h3>

                        <div className="mt-4 space-y-4">
                          {/* Name */}
                          <div>
                            <label className="block text-sm font-medium text-gray-700">Configuration Name</label>
                            <input
                              type="text"
                              value={configName}
                              onChange={(e) => setConfigName(e.target.value)}
                              className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                              placeholder="e.g., Tech Startup Leads"
                              required
                            />
                          </div>

                          {/* Description */}
                          <div>
                            <label className="block text-sm font-medium text-gray-700">Description (Optional)</label>
                            <textarea
                              value={description}
                              onChange={(e) => setDescription(e.target.value)}
                              rows={2}
                              className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                              placeholder="Describe what this configuration will search for..."
                            />
                          </div>

                          {/* Industry */}
                          <div>
                            <label className="block text-sm font-medium text-gray-700">Industry</label>
                            <select
                              value={selectedIndustry}
                              onChange={(e) => setSelectedIndustry(e.target.value)}
                              className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                            >
                              <option value="">Select an industry</option>
                              {industries?.map((industry) => (
                                <option key={industry.id} value={industry.name}>
                                  {industry.name}
                                </option>
                              ))}
                            </select>
                          </div>

                          {/* Keywords */}
                          <div>
                            <label className="block text-sm font-medium text-gray-700">
                              Keywords to Search For
                            </label>
                            <div className="mt-1 space-y-2">
                              {keywords.map((keyword, index) => (
                                <div key={index} className="flex space-x-2">
                                  <input
                                    type="text"
                                    value={keyword}
                                    onChange={(e) => updateKeyword(index, e.target.value)}
                                    className="flex-1 border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                                    placeholder="e.g., software development, web app, startup"
                                  />
                                  {keywords.length > 1 && (
                                    <button
                                      type="button"
                                      onClick={() => removeKeyword(index)}
                                      className="p-2 text-red-400 hover:text-red-600"
                                    >
                                      <TrashIcon className="h-4 w-4" />
                                    </button>
                                  )}
                                </div>
                              ))}
                              <button
                                type="button"
                                onClick={addKeyword}
                                className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                              >
                                <PlusIcon className="h-4 w-4 mr-2" />
                                Add Keyword
                              </button>
                            </div>
                          </div>

                          {/* Sources */}
                          <div>
                            <label className="block text-sm font-medium text-gray-700">
                              Sources to Search (Optional)
                            </label>
                            <div className="mt-1 space-y-2">
                              {sources.map((source, index) => (
                                <div key={index} className="flex space-x-2">
                                  <input
                                    type="text"
                                    value={source}
                                    onChange={(e) => updateSource(index, e.target.value)}
                                    className="flex-1 border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                                    placeholder="e.g., techcrunch.com, linkedin.com"
                                  />
                                  {sources.length > 1 && (
                                    <button
                                      type="button"
                                      onClick={() => removeSource(index)}
                                      className="p-2 text-red-400 hover:text-red-600"
                                    >
                                      <TrashIcon className="h-4 w-4" />
                                    </button>
                                  )}
                                </div>
                              ))}
                              <button
                                type="button"
                                onClick={addSource}
                                className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                              >
                                <PlusIcon className="h-4 w-4 mr-2" />
                                Add Source
                              </button>
                            </div>
                          </div>

                          {/* Frequency and Settings */}
                          <div className="grid grid-cols-2 gap-4">
                            <div>
                              <label className="block text-sm font-medium text-gray-700">Frequency</label>
                              <select
                                value={frequency}
                                onChange={(e) => setFrequency(e.target.value)}
                                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                              >
                                <option value="hourly">Hourly</option>
                                <option value="daily">Daily</option>
                                <option value="weekly">Weekly</option>
                                <option value="monthly">Monthly</option>
                              </select>
                            </div>
                            <div>
                              <label className="block text-sm font-medium text-gray-700">Max Results per Run</label>
                              <input
                                type="number"
                                value={maxResults}
                                onChange={(e) => setMaxResults(parseInt(e.target.value))}
                                min="1"
                                max={getSubscriptionLimits().maxResults}
                                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                              />
                              <p className="mt-1 text-xs text-gray-500">
                                Max: {getSubscriptionLimits().maxResults} (based on your plan)
                              </p>
                            </div>
                          </div>

                          {/* Location and Search Query */}
                          <div className="grid grid-cols-2 gap-4">
                            <div>
                              <label className="block text-sm font-medium text-gray-700">Location (Optional)</label>
                              <input
                                type="text"
                                value={location}
                                onChange={(e) => setLocation(e.target.value)}
                                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                                placeholder="e.g., San Francisco, CA"
                              />
                            </div>
                            <div>
                              <label className="block text-sm font-medium text-gray-700">Custom Search Query (Optional)</label>
                              <input
                                type="text"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-primary-500 focus:border-primary-500"
                                placeholder="e.g., 'hiring developers' OR 'seeking contractors'"
                              />
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                    <button
                      type="submit"
                      disabled={createMutation.isLoading || updateMutation.isLoading}
                      className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-primary-600 text-base font-medium text-white hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 sm:ml-3 sm:w-auto sm:text-sm disabled:opacity-50"
                    >
                      {createMutation.isLoading || updateMutation.isLoading ? 'Saving...' : (editingConfig ? 'Update' : 'Create')}
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        setIsCreateModalOpen(false);
                        setIsEditModalOpen(false);
                        setEditingConfig(null);
                        resetForm();
                      }}
                      className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default ScrapingConfigs;
